INSERT INTO `z_tutor` VALUES ('13', '大宝法王', '/zmo/upload/2015/01/10/02/cdbcbcb8069a0e099e201a73b6843b12.jpg', '/zmo/upload/2015/01/10/02/cdbcbcb8069a0e099e201a73b6843b12_250_285.jpg', '', '活佛', '活佛', '<div class=\"mod-top\" id=\"card-container\" style=\"color:#000000;font-family:arial, 宋体, sans-serif;font-size:14px;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<div class=\"card-summary nslog-area clearfix\" style=\"font-size:14px;color:#333333;background-color:#FFFFFF;\">\r\n		<div class=\"card-summary-content\">\r\n			<div class=\"para\" style=\"color:#333333;margin:15px 0px 5px;\">\r\n				“<a target=\"_blank\" href=\"http://baike.baidu.com/view/89598.htm\">大宝</a><a target=\"_blank\" href=\"http://baike.baidu.com/view/732226.htm\">法王</a>”尊号<a target=\"_blank\" href=\"http://baike.baidu.com/view/343706.htm\">起源</a>于第五世噶玛巴<a target=\"_blank\" href=\"http://baike.baidu.com/view/5177444.htm\">德新谢巴</a>，由<a target=\"_blank\" href=\"http://baike.baidu.com/view/23538.htm\">明成祖</a><a target=\"_blank\" href=\"http://baike.baidu.com/view/15053.htm\">永乐皇帝</a>赐封，成为噶玛巴的专有称号，一直沿袭至今。大宝法王是<a target=\"_blank\" href=\"http://baike.baidu.com/view/5225.htm\">藏传佛教</a><a target=\"_blank\" href=\"http://baike.baidu.com/view/14056.htm\">噶举派</a>（<a target=\"_blank\" href=\"http://baike.baidu.com/view/682444.htm\">白教</a>）的最高<a target=\"_blank\" href=\"http://baike.baidu.com/view/35474.htm\">活佛</a>，在整个<a target=\"_blank\" href=\"http://baike.baidu.com/view/5225.htm\">藏传佛教</a>界，地位仅次于<a target=\"_blank\" href=\"http://baike.baidu.com/view/13934.htm\">格鲁派</a>（<a target=\"_blank\" href=\"http://baike.baidu.com/view/254198.htm\">黄教</a>）的嘉瓦<a target=\"_blank\" href=\"http://baike.baidu.com/view/683302.htm\">仁波切</a>和<a target=\"_blank\" href=\"http://baike.baidu.com/view/26773.htm\">班禅额尔德尼</a>。大宝法王同时是藏传佛教活佛<a target=\"_blank\" href=\"http://baike.baidu.com/view/75628.htm\">转世</a>制度的创始者。噶玛巴的法脉传承从第一世噶玛巴杜松虔巴（1110年诞生）开始，一直不间断地<a target=\"_blank\" href=\"http://baike.baidu.com/subview/1716584/11098075.htm\">传承</a>至今，现在的尊胜第十七世大宝法王<a target=\"_blank\" href=\"http://baike.baidu.com/view/3427743.htm\">噶玛巴·邬金钦列多杰</a>是噶举传承的最高精神领袖。\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<div class=\"baseInfoWrap\" id=\"baseInfoWrapDom\" style=\"font-size:12px;margin:0px 0px -6px;color:#000000;font-family:arial, 宋体, sans-serif;font-style:normal;font-weight:normal;text-align:start;background:url(http://baike.bdimg.com/static/widget/lemma/baseInfoBar/img/baseInfoBarBg_8955c10c.png) #FFFFFF;\">\r\n	<div class=\"baseInfoLeft\">\r\n		<div class=\"biItem\">\r\n			<div class=\"biItemInner\">\r\n				<br class=\"Apple-interchange-newline\" />\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>', '0', '0', '0', '0', '0');
